// Function to create and append elements with attributes
function createElement(tag, attributes = {}, content = '') {
    const element = document.createElement(tag);
    for (const key in attributes) {
        element.setAttribute(key, attributes[key]);
    }
    if (content) {
        element.textContent = content;
    }
    return element;
}

// Title and page content
document.title = "How To Add Google Translate Button On Your Webpage ?";
document.body.append(
    createElement('p', {}, 'Hello everyone!'),
    createElement('p', {}, 'Welcome to GeeksforGeeks'),
    createElement('p', {}, 'Translate this page in your preferred language:')
);

// Google Translate Element
const translateDiv = createElement('div', { id: 'google_translate_element' });
document.body.appendChild(translateDiv);

// Google Translate script
const translateScript = createElement('script', { 
    type: 'text/javascript',
    src: 'https://translate.google.com/translate_a/element.js?cb=googleTranslateElementInit' 
});
document.body.appendChild(translateScript);

// Additional content
document.body.append(
    createElement('p', {}, 'You can translate the content of this page by selecting a language in the select box.')
);

// Function to initialize Google Translate Element
function googleTranslateElementInit() {
    new google.translate.TranslateElement({ pageLanguage: 'en' }, 'google_translate_element');
}

// Call the function to initialize Google Translate Element
googleTranslateElementInit();
