// Create a function to initialize Google Translate Element
function googleTranslateElementInit() {
    new google.translate.TranslateElement(
        { pageLanguage: 'en' },
        'google_translate_element'
    );
}

// Create the elements dynamically
const head = document.createElement('head');
const title = document.createElement('title');
const body = document.createElement('body');
const p1 = document.createElement('p');
const p2 = document.createElement('p');
const p3 = document.createElement('p');
const div = document.createElement('div');
const script1 = document.createElement('script');
const script2 = document.createElement('script');
const p4 = document.createElement('p');

// Set the content for the elements
title.textContent = "How To Add Google Translate Button On Your Webpage ?";
p1.textContent = "Hello everyone!";
p2.textContent = "Welcome to GeeksforGeeks";
p3.textContent = "Translate this page in your preferred language:";
p4.textContent = "You can translate the content of this page by selecting a language in the select box.";

// Set attributes for the elements
div.id = "google_translate_element";
script1.type = "text/javascript";
script1.src = "https://translate.google.com/translate_a/element.js?cb=googleTranslateElementInit";

// Append elements to the DOM
document.appendChild(head);
document.appendChild(body);
head.appendChild(title);
body.appendChild(p1);
body.appendChild(p2);
body.appendChild(p3);
body.appendChild(div);
body.appendChild(script1);
body.appendChild(script2);
body.appendChild(p4);

// Call the function to initialize Google Translate Element
googleTranslateElementInit();
